from typing import List

class Solution:
    def removeDuplicates(self, arr: List[int]) -> int:
        if len(arr) <= 2:
            return len(arr)

        prev = 1  
        count = 1

        for cur in range(1, len(arr)):
            if arr[cur] == arr[cur - 1]:
                count += 1
            else:
                count = 1 

            if count <= 2:
                arr[prev] = arr[cur]
                prev += 1

        return prev

class Solution:
    def findDuplicate(self, nums: List[int]) -> int:
        slow = nums[0]
        fast = nums[0]
        while True:
            slow = nums[slow]
            fast = nums[nums[fast]]
            if slow == fast:
                break
        fast = nums[0]
        while slow != fast:
            slow = nums[slow]
            fast = nums[fast]
        return slow

class Solution:
    def duplicateZeros(self, arr: List[int]) -> None:
        n = len(arr)
        zeros = 0
        
        # Count how many zeros to duplicate within bounds
        for i in range(n):
            if i + zeros >= n - 1:
                # Handle edge zero
                if i + zeros == n - 1 and arr[i] == 0:
                    arr[n - 1] = 0
                    n -= 1  # Do not duplicate this zero
                break
            if arr[i] == 0:
                zeros += 1
        
        # Second pass: go backwards and shift elements
        for i in range(n - 1 - zeros, -1, -1):
            if arr[i] == 0:
                arr[i + zeros] = 0
                zeros -= 1
                arr[i + zeros] = 0
            else:
                arr[i + zeros] = arr[i]

